mapper.select:
select t.type,count(t.customer_id) as customerNum,count(t.dealCustomer) as dealCustomerNum
from (
    select DATE_FORMAT(a.create_time, #{sqlDateFormat}) as type,
    if(a.deal_status ='1',1,null) as dealCustomer,
    a.customer_id
    from wk_crm_customer as a
    where DATE_FORMAT(a.create_time, #{sqlDateFormat}) between #{beginTime} and #{finalTime}
    and a.owner_user_id in

        #{item}

    and a.status != 3
) as t
group by t.type

select.foreach:
select '${x}' as type, IFNULL((select count(1) from wk_crm_activity a left join wk_crm_customer c on a.activity_type_id = c.customer_id where if(a.activity_type = 2,c.status != 3,1=1) and DATE_FORMAT(a.create_time,'${sqlDateFormat}') = ${x} and a.type = 1 and a.create_user_id in
#{item}
),0) as recordCount, (SELECT count(v.customer_id) from (SELECT a.activity_type_id as customer_id FROM wk_crm_activity a left join wk_crm_customer c on a.activity_type_id = c.customer_id WHERE c.status != 3 and a.activity_type = 2 AND a.type = 1 AND DATE_FORMAT(a.create_time, '${sqlDateFormat}') = ${x} AND a.create_user_id in
#{item}
union SELECT a.customer_id FROM wk_crm_leads a LEFT JOIN wk_crm_activity b ON a.leads_id = b.activity_type_id AND activity_type = 1 AND type = 1 left join wk_crm_customer c on b.activity_type_id = c.customer_id WHERE c.status != 3 and DATE_FORMAT(b.create_time, '${sqlDateFormat}') = ${x} AND b.create_user_id in
#{item}
union SELECT a.customer_id FROM wk_crm_contacts a LEFT JOIN wk_crm_activity b ON a.contacts_id = b.activity_type_id AND activity_type = 3 AND type = 1 left join wk_crm_customer c on b.activity_type_id = c.customer_id WHERE c.status != 3 and DATE_FORMAT(b.create_time, '${sqlDateFormat}') = ${x} AND b.create_user_id in
#{item}
union SELECT a.customer_id FROM wk_crm_business a LEFT JOIN wk_crm_activity b ON a.business_id = b.activity_type_id AND activity_type = 5 AND type = 1 AND b.create_user_id in
#{item}
left join wk_crm_customer c on b.activity_type_id = c.customer_id WHERE c.status != 3 and DATE_FORMAT(b.create_time, '${sqlDateFormat}') = ${x} ) as v) AS customerCount

mapper.select:
select cau.realname, ( select count(1) from `wk_crm_activity` a left join wk_crm_customer c on a.activity_type_id = c.customer_id where if(a.activity_type = 2,c.status != 3,1=1) and a.create_user_id = v.create_user_id and DATE_FORMAT(a.create_time,'${sqlDateFormat}') between #{beginTime} and #{finalTime} and a.type = 1 ) as recordCount, count(v.customer_id) as customerCount from (SELECT a.activity_type_id as customer_id,a.create_user_id FROM wk_crm_activity a left join wk_crm_customer c on a.activity_type_id = c.customer_id WHERE c.status != 3 and a.activity_type = 2 AND a.type = 1 AND DATE_FORMAT(a.create_time, '${sqlDateFormat}') between #{beginTime} and #{finalTime} union SELECT a.customer_id,b.create_user_id FROM wk_crm_leads a LEFT JOIN wk_crm_activity b ON a.leads_id = b.activity_type_id AND activity_type = 1 AND type = 1 left join wk_crm_customer c on b.activity_type_id = c.customer_id WHERE c.status != 3 and DATE_FORMAT(b.create_time, '${sqlDateFormat}') between #{beginTime} and #{finalTime} union SELECT a.customer_id,b.create_user_id FROM wk_crm_contacts a LEFT JOIN wk_crm_activity b ON a.contacts_id = b.activity_type_id AND activity_type = 3 AND type = 1 left join wk_crm_customer c on b.activity_type_id = c.customer_id WHERE c.status != 3 and DATE_FORMAT(b.create_time, '${sqlDateFormat}') between #{beginTime} and #{finalTime} union SELECT a.customer_id,b.create_user_id FROM wk_crm_business a LEFT JOIN wk_crm_activity b ON a.business_id = b.activity_type_id AND activity_type = 5 AND type = 1 left join wk_crm_customer c on b.activity_type_id = c.customer_id WHERE c.status != 3 and DATE_FORMAT(b.create_time, '${sqlDateFormat}') between #{beginTime} and #{finalTime} ) as v left JOIN wk_admin_user cau on cau.user_id = v.create_user_id WHERE cau.user_id in
#{item}
GROUP BY cau.user_id

select a.category,IFNULL(count(a.activity_id),0) as recordNum,
TRUNCATE(IFNULL(count(a.activity_id)*100/(select count(*) from wk_crm_activity a
left join wk_crm_customer c on a.activity_type_id = c.customer_id
where c.status != 3 and a.type = 1
and DATE_FORMAT(a.create_time,#{sqlDateFormat}) between #{beginTime} and #{finalTime}
and a.create_user_id in

    #{item}

),0),2) as proportion
from wk_crm_activity a
left join wk_crm_customer c on a.activity_type_id = c.customer_id
where c.status != 3 and a.type = 1 and DATE_FORMAT(a.create_time,#{sqlDateFormat}) between #{beginTime} and #{finalTime}
and a.create_user_id in

    #{item}

group by a.category

select a.customer_name, case when b.contract_id is NULL then null when b.contract_id is not null then b.name end as contract_name, case when b.contract_id is NULL then null when b.contract_id is not null then b.money end as contract_money, case when b.contract_id is NULL then null when b.contract_id is not null then b.order_date end as order_date, (select sum(c.money) from wk_crm_receivables as c where c.check_status =1 and c.contract_id = b.contract_id ) as receivables_money, d.realname AS owner_user_name, a.create_time,a.update_time from wk_crm_customer as a left join wk_crm_contract as b on a.customer_id = b.customer_id left join wk_admin_user as d on a.owner_user_id = d.user_id where DATE_FORMAT(a.create_time,#{sqlDateFormat}) = #{time} and b.check_status != 7 and a.deal_status = 1 and a.owner_user_id in
#{item}
and a.status != 3

select.foreach:
