select.foreach:
SELECT '${x}' as address, COUNT(1) as allCustomer, sum(deal_status) as dealCustomer FROM wk_crm_customer as ccc where left(ccc.address,INSTR(ccc.address, ',') - 1) like concat('%','${x}','%') and ccc.owner_user_id in
#{item}
and DATE_FORMAT(ccc.create_time,'${sqlDateFormat}') between #{beginTime} and #{finalTime}

mapper.select:
SELECT IFNULL(SUM(cct.money), 0) as money,cau.realname,cau.user_id,cau.img,cct.owner_user_id,cad.name as structureName FROM wk_crm_contract as cct LEFT JOIN wk_admin_user as cau on cau.user_id = cct.owner_user_id left join wk_admin_dept as cad on cad.dept_id = cau.dept_id WHERE check_status = 1 and cct.owner_user_id in
#{item}
and DATE_FORMAT(order_date,'${sqlDateFormat}') between #{beginTime} and #{finalTime} GROUP BY cct.owner_user_id ORDER BY money DESC

SELECT IFNULL(SUM(cct.money), 0) as money,cau.realname,cau.user_id,cau.img,cad.name as structureName FROM wk_crm_receivables as cct LEFT JOIN wk_admin_user as cau on cau.user_id = cct.owner_user_id left join wk_admin_dept as cad on cad.dept_id = cau.dept_id WHERE check_status = 1 and cct.owner_user_id in
#{item}
and DATE_FORMAT(return_time,'${sqlDateFormat}') between #{beginTime} and #{finalTime} GROUP BY cct.owner_user_id ORDER BY money DESC

SELECT count(1) as `count`,cau.realname,cau.user_id,cau.img,cct.owner_user_id,cad.name as structureName FROM wk_crm_contract as cct LEFT JOIN wk_admin_user as cau on cau.user_id = cct.owner_user_id left join wk_admin_dept as cad on cad.dept_id = cau.dept_id WHERE check_status != 7 and cct.owner_user_id in
#{item}
and DATE_FORMAT(order_date,'${sqlDateFormat}') between #{beginTime} and #{finalTime} GROUP BY cct.owner_user_id ORDER BY count DESC

SELECT count(1) as `count`,cau.realname,cct.owner_user_id,cad.name as structureName FROM wk_crm_contract_product as ccp LEFT JOIN wk_crm_contract as cct on ccp.contract_id = cct.contract_id LEFT JOIN wk_admin_user as cau on cau.user_id = cct.owner_user_id left join wk_admin_dept as cad on cad.dept_id = cau.dept_id WHERE check_status = 1 and cct.owner_user_id in
#{item}
and DATE_FORMAT(order_date,'${sqlDateFormat}') between #{beginTime} and #{finalTime} GROUP BY cct.owner_user_id ORDER BY count DESC

SELECT count(1) as `count`,cau.realname,cau.user_id,cau.img,cct.owner_user_id,cad.name as structureName FROM wk_crm_customer as cct LEFT JOIN wk_admin_user as cau on cau.user_id = cct.owner_user_id left join wk_admin_dept as cad on cad.dept_id = cau.dept_id WHERE cct.status != 3 and cct.owner_user_id in
#{item}
and DATE_FORMAT(cct.create_time,'${sqlDateFormat}') between #{beginTime} and #{finalTime} GROUP BY cct.owner_user_id ORDER BY count DESC

SELECT count(1) as `count`,cau.realname,cau.user_id,cau.img,cct.create_user_id,cad.name as structureName FROM wk_crm_contacts as cct LEFT JOIN wk_admin_user as cau on cau.user_id = cct.create_user_id left join wk_admin_dept as cad on cad.dept_id = cau.dept_id WHERE cct.create_user_id in
#{item}
and DATE_FORMAT(cct.create_time,'${sqlDateFormat}') between #{beginTime} and #{finalTime} GROUP BY cct.create_user_id ORDER BY count DESC

select cau.realname, cau.user_id, cad.name AS structureName, count(v.customer_id) as count from (SELECT a.activity_type_id as customer_id,a.create_user_id FROM wk_crm_activity a left join wk_crm_customer c on a.activity_type_id = c.customer_id WHERE c.status != 3 AND a.activity_type = 2 AND a.type = 1 AND DATE_FORMAT(a.create_time, '${sqlDateFormat}') between #{beginTime} and #{finalTime} union SELECT a.customer_id,b.create_user_id FROM wk_crm_leads a LEFT JOIN wk_crm_activity b ON a.leads_id = b.activity_type_id AND activity_type = 1 AND type = 1 left join wk_crm_customer c on b.activity_type_id = c.customer_id WHERE c.status != 3 AND DATE_FORMAT(b.create_time, '${sqlDateFormat}') between #{beginTime} and #{finalTime} union SELECT a.customer_id,b.create_user_id FROM wk_crm_contacts a LEFT JOIN wk_crm_activity b ON a.contacts_id = b.activity_type_id AND activity_type = 3 AND type = 1 left join wk_crm_customer c on b.activity_type_id = c.customer_id WHERE c.status != 3 AND DATE_FORMAT(b.create_time, '${sqlDateFormat}') between #{beginTime} and #{finalTime} union SELECT a.customer_id,b.create_user_id FROM wk_crm_business a LEFT JOIN wk_crm_activity b ON a.business_id = b.activity_type_id AND activity_type = 5 AND type = 1 left join wk_crm_customer c on b.activity_type_id = c.customer_id WHERE c.status != 3 AND DATE_FORMAT(b.create_time, '${sqlDateFormat}') between #{beginTime} and #{finalTime} ) as v left JOIN wk_admin_user cau on cau.user_id = v.create_user_id inner JOIN wk_admin_dept cad ON cad.dept_id = cau.dept_id WHERE cau.user_id in
#{item}
GROUP BY cau.user_id order by count desc

SELECT count(1) as `count`,cau.realname,cau.user_id,cau.img,ccr.create_user_id,cad.name as structureName FROM wk_crm_activity as ccr LEFT JOIN wk_admin_user as cau on cau.user_id = ccr.create_user_id left join wk_admin_dept as cad on cad.dept_id = cau.dept_id left join wk_crm_customer c on ccr.activity_type_id = c.customer_id WHERE if(ccr.activity_type = 2,c.status != 3,1=1) and ccr.type = 1 and ccr.create_user_id in
#{item}
and DATE_FORMAT(ccr.create_time,'${sqlDateFormat}') between #{beginTime} and #{finalTime} GROUP BY ccr.create_user_id ORDER BY count DESC

SELECT count(1) as `count`,cau.realname,coe.create_user_id,cad.name as structureName FROM wk_oa_examine_travel as coet LEFT JOIN wk_oa_examine as coe on coe.examine_id = coet.examine_id left join `wk_oa_examine_category` a on coe.category_id = a.category_id LEFT JOIN wk_admin_user as cau on cau.user_id = coe.create_user_id left join wk_admin_dept as cad on cad.dept_id = cau.dept_id WHERE a.type = 3 and coe.create_user_id in
#{item}
and coe.examine_status = 1 and DATE_FORMAT(coet.start_time,'${sqlDateFormat}') between #{beginTime} and #{finalTime} GROUP BY coe.create_user_id ORDER BY count DESC

SELECT IFNULL(SUM(cct.money), 0) as money,(TRUNCATE ( IFNULL((IFNULL(SUM(cct.money), 0)/SUM(achievement)),0)*100 ,2)) as rate, cau.realname,cau.user_id,cau.img,cct.owner_user_id,cad.name as structureName FROM (select sum(money) as money, owner_user_id from wk_crm_contract where check_status = 1 and owner_user_id in
#{item}
and DATE_FORMAT(order_date,'${data.sqlDateFormat}') between #{data.beginTime} and #{data.finalTime} group by owner_user_id) as cct LEFT JOIN wk_admin_user as cau on cau.user_id = cct.owner_user_id left join wk_admin_dept as cad on cad.dept_id = cau.dept_id left join ( select sum(x.achievement) as achievement,x.user_id from (
select ${x.month} as achievement,obj_id as user_id from wk_crm_achievement where obj_id in
#{item}
and year = #{x.year} and type = 3 and status = 1
) as x group by user_id) as y ON y.user_id = cct.owner_user_id where 1=1 GROUP BY cct.owner_user_id ORDER BY money DESC

SELECT IFNULL(SUM(cct.money), 0) as money,(TRUNCATE ( IFNULL((IFNULL(SUM(cct.money), 0)/SUM(achievement)),0)*100 ,2)) as rate, cau.realname,cau.user_id,cau.img,cad.name as structureName FROM (select sum(money) as money, owner_user_id from wk_crm_receivables where check_status = 1 and owner_user_id in
#{item}
and DATE_FORMAT(return_time,'${data.sqlDateFormat}') between #{data.beginTime} and #{data.finalTime} group by owner_user_id) as cct LEFT JOIN wk_admin_user as cau on cau.user_id = cct.owner_user_id left join wk_admin_dept as cad on cad.dept_id = cau.dept_id left join ( select sum(x.achievement) as achievement,x.user_id from (
select ${x.month} as achievement,obj_id as user_id from wk_crm_achievement where obj_id in
#{item}
and year = #{x.year} and type = 3 and status = 2
) as x group by user_id) as y ON y.user_id = cct.owner_user_id where 1=1 GROUP BY cct.owner_user_id ORDER BY money DESC
