mapper.select:
SELECT b.realname,b.username,b.img,b.user_id,
(SELECT COUNT(1) FROM wk_work_task_comment WHERE type='2' and type_id=a.log_id) as sum,a.send_user_ids,a.read_user_ids
FROM wk_oa_log as a
LEFT JOIN wk_admin_user as b on a.create_user_id=b.user_id
WHERE DATE_FORMAT(a.create_time,#{sqlDateFormat}) between #{beginTime}
and #{finalTime} and a.create_user_id =#{userId}

SELECT a.user_id,a.username,a.realname,a.img
,COUNT(case when b.category_id=#{item.examineId} then 1 end) as count_${item.examineId}
FROM wk_admin_user AS a LEFT JOIN wk_oa_examine AS b ON a.user_id = b.create_user_id and (DATE_FORMAT(b.create_time,#{sqlDateFormat}) between #{beginTime} and #{finalTime}) where a.user_id in
#{item}
and b.category_id in
#{item.examineId}
and b.examine_status = 1 GROUP BY a.user_id
