mapper.select:
select count(*) as allCount, count((status=1 and is_archive=0)or null) as unfinished, count((status=2 and is_archive=0) or null) as overdue, count((status=5 and is_archive=0) or null) as complete, count(is_archive=1 or null) as archive, ifnull(ROUND((count((status=5 and is_archive=0) or null)/count(is_archive=0 or null))*100,2),0) as completionRate, ifnull(ROUND((count((status=2 and is_archive=0) or null)/count(is_archive=0 or null))*100,2),0) as overdueRate from wk_work_task where ishidden = 0 and work_id != 0
and work_id = #{workId}
and work_id in
#{id}
and (main_user_id = #{userId} or owner_user_id like concat('%,',#{userId},',%')) and (is_archive = 0 or (is_archive = 1 and status = 5))
and main_user_id = #{mainUserId}

SELECT a.realm,a.menu_id,a.parent_id FROM wk_admin_menu as a
LEFT JOIN wk_admin_role_menu as b on a.menu_id=b.menu_id
where b.role_id = #{roleId}
